import { ArrayDetails, EntityDetails, ObjectDetails, PrimitiveFieldDetails } from './schema-details';

/** Schema location details are the same as schema path details, except they indicate for arrays what index
 * they're pointing on.
 */
export type SchemaLocationDetails = EntityDetails | ArrayDetails | (PrimitiveFieldDetails & {
    index?: number;
}) | (ObjectDetails & {
    index?: number;
});
