import { Observable } from './observable-notifier';
import { ObservableValue, ValueNotifier } from './observable-notifier-value';

/** Takes a series of boolean notifiers, creates a boolean observable
 * that is always the AND of the inputted notifiers.
 */
export declare const combinedValueNotifiersWithAnd: (...notifiers: ValueNotifier<boolean>[]) => ObservableValue<boolean>;
/** Takes a set of observables, and merges them into one, notifying if any of
 * the observables emits a value.
 *
 * The type is slightly complicated, but it just merges the observables type using `|`.
 */
export declare const combineObservables: <T extends Observable<unknown>[]>(...observables: T) => Observable<T[number] extends Observable<infer U> ? U : never>;
