import { Terminable } from './terminable';

export type Sign = -1 | 0 | 1;
export type Nullish<T = never> = T | undefined | null;
export type Class<T = object> = Function & {
    prototype: T;
};
export type Comparator<T> = (a: T, b: T) => number;
/** "lifts" a comparator to lists, i.e., given a comparator for type `T`, this returns a comparator for type `T[]`. */
export declare const ListComparator: <T>(comparator: Comparator<T>) => Comparator<T[]>;
export type Comparable<T> = {
    compareTo: (other: T) => number;
};
export type Equality<T> = {
    equals: (other: T) => boolean;
};
/** Instantly throws the message or error. `string`s are wrapped in an `Error`.
 *
 * Useful to throw early if something is expected to be defined using
 * the `??` operator. Example:
 * ```
 * const getX: () => number | undefined
 * const x: number = getX() ?? throw_("expected x to be defined")
 * ```
 */
export declare const throw_: (message?: string | Error) => never;
/** A generic assert function. At compile time, tells typescript to assume the condition
 * to be true, and at runtime, throws if the condition is false.
 */
export declare function assert(condition: boolean, msg?: string): asserts condition;
/** Creates a promise that resolves after (approximately) `ms` milliseconds */
export declare const sleep: (ms: number) => Promise<void>;
/** Works like {@link setInterval} but accepts async functions which are awaited
 * until completion before going to sleep for `intervalMs` ms. The callback is executed
 * immediately unless `immediateTrigger  = false` is passed.
 */
export declare const asyncInterval: (callback: () => Promise<void>, intervalMs: number, opts?: {
    immediateTrigger?: boolean;
}) => Terminable;
