export type LoggedInStatus = {
    /** The app is authorized to make actions on a user's behalf. */
    loggedIn: true;
    /** Simple utility to get the current user display name / user name. The result of this function is cached. */
    getUserInfo(): Promise<{
        name: string;
        displayName: string;
    } | Error>;
    /** Log the current user out and reload the page. */
    logout: () => void;
    /** Get the current authentication token. Might refresh the token if need be, but most often
     * just returns the token.
     */
    getToken: () => Promise<string | Error>;
};
/**
 * The current authentication status of the user in this tab. Either logged in or logged out.
 * To change the authentication status, call login or logout on this object.
 */
export type LoginStatus = {
    /** The app is authorized to make actions on a user's behalf. */
    loggedIn: true;
    /** Simple utility to get the current user name. The result of this function is cached. */
    getUserName(): Promise<string | Error>;
    /** Log the current user out and reload the page. */
    logout: () => void;
    /** Get the current authentication token. Might refresh the token if need be, but most often
     * just returns the token.
     */
    getToken: () => Promise<string | Error>;
} | {
    /** The app is not authorized to make actions on a user's behalf. Call login to authorize.*/
    loggedIn: false;
    /** If an error is the reason the user is logged out, this is set. Otherwise, the user just hasn't logged in yet. */
    error?: Error;
    /** start the authorization flow by redirecting the user to the login page of audiotool. */
    login: () => Promise<void>;
};
/**
 * Get the current authentication status of the user in this tab. Either logged in or logged out.
 *
 * This function should only be called once, and the result should be considered definite.
 *
 * If it should change, reload the tab, or call login/logout on the returned object.
 *
 * You should call this function early in your app, since the user likely won't be able to do anything
 * until they're logged in. If they're logged in, you can continue initializing the rest of the app. If they're
 * logged out, show a button they can press that will call login.
 *
 * Show the error to the user, if set.
 *
 * Under the hood, this function will read the URL query parameters and clean them up, as well as manage authentication
 * tokens in localStorage, and automatically refresh them if expired.
 */
export declare const getLoginStatus: ({ clientId, redirectUrl, scope, }: {
    clientId: string;
    redirectUrl: string;
    scope: string;
}) => Promise<LoginStatus>;
